/// Runs the FIPS 140-3 KAT for Dilithium-3 verify.
pub fn run_dilithium_verify_kat() -> Result<(), FipsError> {
    
    // Call the verification function
    let result = pqc_dilithium_verify(&KAT_PK, &KAT_MSG, &KAT_SIG);

    // Check that verification succeeded
    match result {
        Ok(_) => Ok(()),
        Err(_) => {
            // Failure! Enter FIPS error state.
            set_fips_error_state(FipsError::PostDilithiumVerifyKatFailed);
            Err(FipsError::PostDilithiumVerifyKatFailed)
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::fips_state::initialize_fips_state;

    #[test]
    fn test_dilithium_kat() {
        initialize_fips_state();
        assert_eq!(run_dilithium_verify_kat(), Ok(()));
    }
}